import{_ as t,c as r,a1 as o,o as d}from"./chunks/framework.C7abYapc.js";const p=JSON.parse('{"title":"ðŸ”– Discord Webhooks","description":"","frontmatter":{"title":"ðŸ”– Discord Webhooks"},"headers":[],"relativePath":"rusty-connector/docs/concepts/discord-webhooks.md","filePath":"rusty-connector/docs/concepts/discord-webhooks.md"}'),a={name:"rusty-connector/docs/concepts/discord-webhooks.md"};function s(c,e,l,i,h,n){return d(),r("div",null,e[0]||(e[0]=[o('<p>Discord webhooks allow you to send alerts directly to Discord channels. All webhook messages are referred to as &quot;alerts.&quot; When a particular event occurs, an alert will be sent to the proper webhooks.</p><p>:::note Privacy Discord Webhooks will NEVER include private details such as IP Addresses in alerts! Instead they will use the family and server names that you set in your config files! Because of this, you should make sure you are setting human-readable names in your RC configurations. :::</p><h2 id="scope" tabindex="-1">Scope <a class="header-anchor" href="#scope" aria-label="Permalink to &quot;Scope&quot;">â€‹</a></h2><p>You can set the scope of a webhook using the scope flag. Available options are:</p><table tabindex="0"><thead><tr><th>Scope</th><th>Description</th></tr></thead><tbody><tr><td><code>Proxy</code></td><td>The proxy scope, alerts will be sent no matter what.</td></tr><tr><td><code>Family</code></td><td>A family scope (the name of the family to target should be provided using the <code>target-family</code> tag.) Alerts will only trigger if they are related to this specific family.</td></tr></tbody></table><p>:::caution Alerts will only fire if they&#39;re used in the correct scope. :::</p><h3 id="scope-proxy" tabindex="-1">Scope: Proxy <a class="header-anchor" href="#scope-proxy" aria-label="Permalink to &quot;Scope: Proxy&quot;">â€‹</a></h3><table tabindex="0"><thead><tr><th>Type</th><th>Alert Name</th><th>Trigger</th></tr></thead><tbody><tr><td>Servers</td><td><code>register_all</code></td><td>When the proxy sends a request for all servers to register themselves.</td></tr><tr><td>Servers</td><td><code>server_register</code></td><td>When a server registers to the proxy.</td></tr><tr><td>Servers</td><td><code>server_unregister</code></td><td>When a server unregisters from the proxy.</td></tr><tr><td>Players</td><td><code>player_join</code></td><td>When a player successfully connects to the proxy.</td></tr><tr><td>Players</td><td><code>player_leave</code></td><td>When a player leaves the proxy.</td></tr><tr><td>Players</td><td><code>player_join_family</code></td><td>When a player joins a family.</td></tr><tr><td>Players</td><td><code>player_leave_family</code></td><td>When a player leaves a family.</td></tr><tr><td>Players</td><td><code>player_switch_server</code></td><td>When a player switches from one server in the proxy to another.</td></tr><tr><td>Players</td><td><code>player_switch_family</code></td><td>When a player switches from one family in the proxy to another.</td></tr><tr><td>Players</td><td><code>disconnect_catch</code></td><td>When a player disconnects or is kicked from a family and the root family catches them before they&#39;re kicked from the proxy.</td></tr></tbody></table><h3 id="scope-family" tabindex="-1">Scope: Family <a class="header-anchor" href="#scope-family" aria-label="Permalink to &quot;Scope: Family&quot;">â€‹</a></h3><table tabindex="0"><thead><tr><th>Type</th><th>Alert Name</th><th>Trigger</th></tr></thead><tbody><tr><td>Servers</td><td><code>register_all</code></td><td>When <code>/rc register family</code> is used.</td></tr><tr><td>Servers</td><td><code>server_register</code></td><td>When a server successfully registers to the family.</td></tr><tr><td>Servers</td><td><code>server_unregister</code></td><td>When a server successfully unregisters from the family.</td></tr><tr><td>Players</td><td><code>player_join</code></td><td>When a player successfully connects to the family.</td></tr><tr><td>Players</td><td><code>player_leave</code></td><td>When a player leaves the family.</td></tr><tr><td>Players</td><td><code>player_switch</code></td><td>When a player switches from one server in the family to another.</td></tr></tbody></table>',10)]))}const f=t(a,[["render",s]]);export{p as __pageData,f as default};
